/*   重设css样式   */
*{box-sizing: border-box;}
*::before,*::after{box-sizing: border-box;}
*{margin: 0;padding: 0}
a{color: inherit;text-decoration: none;}
ul,ol{list-style: none;}
img{max-width: 100%;max-height: 100%;}

/*  页面设置    */
body{background: #eee;}

.globalHeader{
    margin: 20px;
}
.searchForm{
    display: flex;
    justify-content: space-between;
}
@media (min-width:500px){
    .searchForm{max-width: 400px;
    margin-left: auto;
    margin-right: auto;
    }
}
.searchForm>input{
    width: 100%;
    margin-right: 10px;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}
.searchForm>button{
    white-space: nowrap;
    padding: 0 10px;
    border: none;
    background: #02A1FB;
    border-radius: 4px;
    color: #FFFFFF;
    padding: 0 28px;
    font-size: 16;
}
.globalMain{
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}
.siteList{
    display: flex;
    flex-wrap: wrap;
    margin: 20px;
    justify-content: space-between;
}
@media(min-width:500px){
    .globalHeader{
        margin: 60px 0 100px;
    }
    .siteList{
        margin-left: 0;
        margin-right: -10px;    /*负margin*/
        justify-content: flex-start;
    }
}
.siteList>li{
    margin-bottom: 10px;
}
@media(min-width:500px){
    .siteList>li{
        margin-right: 7px;
    }
}
.siteList .site{
    width: 175px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: white;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;    /*鼠标图标出现手形*/
}
.siteList .site >.close{
    position: absolute;
    right: 2px;
    top: 0;
    display: none;
    cursor: default;
}
.siteList .site:hover > .close{
    display: block;
}
.siteList .site>.logo{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}
.siteList .site>.link{
    font-size: 14;
    margin-top: 4px;
}
.siteList .addButton{
    border: 1px solid #ddd;
    background: #FFFFFF;
    border-radius: 10px;
    width: 175px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 20px 0;
}
.siteList .addButton .icon{
    width: 64px;
    height: 64px;
}
.siteList .addButton .icon-wrapper{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}
.siteList .addButton .text{
    font-size: 14;
    margin-top: 4px;
}
