*{box-sizing: border-box;}
*::before,*::after{box-sizing: border-box;}
*{margin: 0;padding: 0}
a{color: inherit;text-decoration: none;}
ul,ol{list-style: none;}
img{max-width: 100%;max-height: 100%;}

/*  页面设置    */
body{background: #eee;}

.globalHeader{
    margin: 20px;
}
.searchForm{
    display: flex;
    justify-content: space-between;
}
.searchForm>input{
    width: 100%;
    margin-right: 10px;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}
.searchForm>button{
    white-space: nowrap;
    padding: 0 10px;
    border: none;
    background: #02A1FB;
    border-radius: 4px;
    color: #FFFFFF;
    padding: 0 28px;
    font-size: 16;
}
.globalMain{

}
.siteList{
    display: flex;
    flex-wrap: wrap;
    margin: 20px;
    justify-content:space-between;
}
.siteList>li{

}
.siteList .site{
    width: 175px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: white;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    padding: 20px 0;
}
.siteList .site>.logo{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}
.siteList .site>.link{
    font-size: 14;
    margin-top: 4px;
}
.siteList .addButton{
    border: 1px solid #ddd;
    background: #FFFFFF;
